import Layout from '../components/Layout';
import Image from 'next/image';
import Link from 'next/link';
import { useState } from 'react';

export default function Resources({ resources }) {
  const [image, setImage] = useState('./dev.png');
  const images = ['/web.png', '/dev.png'];
  const checkForImage = (Category) => {
    if (
      Category === 'WebDevelopment' ||
      Category === 'Frontend' ||
      Category === 'Backend'
    ) {
      return images[0];
    }
    return images[1];
  };
  return (
    <Layout title="Resources" isBottom={true}>
      <h1 className="text-5xl text-center uppercase">Resources</h1>
      <div className="mt-6 grid grid-cols-3 gap-3">
        {resources.map((resource) => {
          const { id, Title, Type, URL, Category } = resource;
          return (
            <div key={id}>
              <Link href={URL} passHref>
                <a target="_blank">
                  <div className="mb-8 p-4 sm:p-6 rounded-lg overflow-hidden shadow-lg bg-white h-auto">
                    <Image
                      src={`${checkForImage(Category)}`}
                      width={24}
                      height={24}
                    />
                    <div className="sm:flex sm:justify-between">
                      <h3 className="text-lg sm:text-2xl max-w-md font-inter">
                        {Title}{' '}
                      </h3>
                    </div>
                    {/* Tags */}
                    <div className="mt-6">
                      <span className="mr-1 sm:mr-2 text-xs sm:text-sm rounded py-2 px-2 sm:px-6 bg-purplePrimary bg-opacity-30 uppercase font-light">
                        {Category}
                      </span>
                      <span className="mr-1 sm:mr-2 text-xs sm:text-sm rounded py-2 px-2 sm:px-6 bg-purplePrimary bg-opacity-30 uppercase font-light">
                        {Type}
                      </span>
                    </div>
                  </div>
                </a>
              </Link>
            </div>
          );
        })}
      </div>
    </Layout>
  );
}

export async function getStaticProps() {
  const res = await fetch('http://hire-juniors-cms.herokuapp.com/resources');
  const resources = await res.json();
  //   console.log(resources);
  return {
    props: { resources },
  };
}
