import algoliasearch from 'algoliasearch/lite';
import {
  InstantSearch,
  Hits,
  SearchBox,
  Pagination,
  Highlight,
  ClearRefinements,
  RefinementList,
  Configure,
} from 'react-instantsearch-dom';
import Layout from '../components/Layout';
import Card from '../components/card';

const searchClient = algoliasearch(
  '0WCFHOO3DZ',
  '977dbc62418f6584b1e1a31488c7b5b9'
);

export default function Home() {
  return (
    <Layout title="Hire Juniors" isBottom={true}>
      <main className="px-4 sm:px-64">
        <div className="my-4">
          <InstantSearch
            indexName="development_jobs"
            searchClient={searchClient}
          >
            <div className="left-panel">
              <ClearRefinements />
              <h2>Filters</h2>
              <RefinementList attribute="filters" />
              <Configure hitsPerPage={8} />
            </div>
            <div className="right-panel">
              <SearchBox />
              <Hits hitComponent={Hit} />
              <Pagination />
            </div>
          </InstantSearch>
        </div>
      </main>
    </Layout>
  );
}

function Hit(props) {
  console.log(props);
  return (
    <ul>
      <Card job={props.hit} />
    </ul>
  );
}

export async function getStaticProps() {
  // Call an external API endpoint to get posts.
  // You can use any data fetching library
  const res = await fetch('https://hire-juniors-cms.herokuapp.com/jobs');
  const jobs = await res.json();
  // console.log(jobs);
  return {
    props: { jobs },
  };
}
