        <a target="_blank">
          <div className="mt-8 p-4 rounded overflow-hidden shadow-md bg-white h-auto">
            <div className="flex justify-between">
              <h3 className="text-2xl">{Position} </h3>
              <ul className="text-sm">
                <li>Company: {Company}</li>
                <li>
                  Location:{' '}
                  <span role="image" aria-label="globe">
                    üåê{' '}
                  </span>
                  {Location}
                </li>
              </ul>
            </div>
          </div>
        </a>
      </Link>
    </li>
  );
}
