import Head from 'next/head';
// import styles from '../styles/Home.module.css';

export default function Home({ jobs }) {
  return (
    <div className="contianer mx-auto">
      <Head>
        <title>Hire Juniors</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main className="px-64 pt-16">
        <h1 className="text-5xl text-center">Welcome to hirejuniors.dev</h1>
        <div className="my-4">
          <ul>
            {jobs.map((job) => (
              <li
                key={job.id}
                className="mt-8 p-4 rounded overflow-hidden shadow-lg bg-white h-auto"
              >
                <div className="flex justify-between">
                  <h3 className="text-2xl">{job.Position}</h3>
                  <span>{job.Location}</span>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </main>
    </div>
  );
}

export async function getStaticProps() {
  // Call an external API endpoint to get posts.
  // You can use any data fetching library
  const res = await fetch('https://hire-junior-devs.herokuapp.com/jobs');
  const jobs = await res.json();
  console.log(jobs);
  return {
    props: { jobs },
  };
}
