import {
  RefinementList,
  Configure,
  ClearRefinements,
  connectRefinementList,
  Highlight,
} from 'react-instantsearch-dom';

const CustomRefinementList = ({
  items,
  isFromSearch,
  refine,
  searchForItems,
  createURL,
  attribute,
}) => (
  <ul>
    {items.map((item) => (
      <li key={item.label}>
        <a
          href={createURL(item.value)}
          style={{ fontWeight: item.isRefined ? 'bold' : '' }}
          onClick={(event) => {
            event.preventDefault();
            refine(item.value);
          }}
        >
          {isFromSearch ? (
            <Highlight attribute="label" hit={item} />
          ) : (
            item.label
          )}{' '}
          ({item.count})
        </a>
      </li>
    ))}
  </ul>
);

const Refinement = connectRefinementList(CustomRefinementList);

const Filter = () => {
  return (
    <div className="mb-8 p-4 sm:p-6 rounded-lg overflow-hidden shadow-lg bg-white h-auto">
      <h2 className="text-lg uppercase text-gray-500 mb-2">Filters</h2>
      <h3>Categories</h3>
      <Refinement attribute="Categories" />
      <h3>Type</h3>
      <RefinementList attribute="Type" />
      <h3>Location</h3>
      <RefinementList attribute="Location" />
      <Configure hitsPerPage={25} />
      <ClearRefinements />
    </div>
  );
};

export default Filter;
